import PropTypes from 'prop-types';
import './SimpleButton.css';

const SimnpleButton = ({ buttonTitle, boldTitle = false }) => {
    const buttonStyle = {
        fontWeight: boldTitle ? 'bold' : 'none',
    }
    return (
        <>
            <button className=".btn" style={buttonStyle}>{buttonTitle}</button>
        </>
    );
}

SimnpleButton.propTypes = {
    buttonTitle: PropTypes.string.isRequired,
    boldTitle: PropTypes.bool
}

export default SimnpleButton;
