import PropTypes from 'prop-types';
import styles from './SimpleButton.css';

const SimnpleButton = ({ buttonTitle, boldTitle = false }) => {
    const fontWeight = boldTitle ? 'bold' : 'none';
    return (
        <>
            <button className={styles.btn} style={{ fontWeight }}>{buttonTitle}</button>
        </>
    );
}

SimnpleButton.PropTypes = {
    buttonTitle: PropTypes.string.isRequired,
    boldTitle: PropTypes.bool
}

export default SimnpleButton;
